'use client';
import { jsx, Fragment } from 'react/jsx-runtime';
import { forwardRef, useContext } from 'react';
import { clsx } from '../../../node_modules/clsx/dist/lite.js';
import { TabsContext } from '../Tabs.js';

const TabContent = forwardRef(({ children, value, className, ...rest }, ref) => {
    const { value: tabsValue } = useContext(TabsContext);
    const active = value == tabsValue;
    return (jsx(Fragment, { children: active && (jsx("div", { className: clsx('fds-tabs__content', className), ref: ref, ...rest, children: children })) }));
});
TabContent.displayName = 'TabContent';

export { TabContent };
