import React from 'react';
import { Button } from '@digdir/designsystemet-react';

export interface CodeSnippetProps {
  code: string;
  language?: string;
  style?: React.CSSProperties;
}

const CodeSnippet: React.FC<CodeSnippetProps> = ({ code, language = 'typescript', style }) => {
  const handleCopy = () => {
    navigator.clipboard.writeText(code);
  };

  return (
    <div style={{ 
      position: 'relative',
      backgroundColor: '#f5f5f5',
      borderRadius: '4px',
      padding: '1rem',
      marginTop: '1rem',
      ...style
    }}>
      <Button
        variant="tertiary"
        size="small"
        onClick={handleCopy}
        style={{
          position: 'absolute',
          top: '0.5rem',
          right: '0.5rem'
        }}
      >
        Copy
      </Button>
      <pre style={{ 
        margin: 0,
        padding: 0,
        overflow: 'auto'
      }}>
        <code>{code}</code>
      </pre>
    </div>
  );
};

export default CodeSnippet;
