'use client';
import { jsx, jsxs } from 'react/jsx-runtime';
import { forwardRef, useContext, useId } from 'react';
import { DropdownMenuContext } from './DropdownMenu.js';
import { Paragraph } from '../Typography/Paragraph/Paragraph.js';

const DropdownMenuGroup = forwardRef(({ children, heading, className, style, ...rest }, ref) => {
    const { size } = useContext(DropdownMenuContext);
    const headingId = useId();
    return (jsx("li", { className: className, style: style, children: jsxs("ul", { ...(heading ? { 'aria-labelledby': headingId } : {}), ref: ref, role: 'group', className: 'fds-dropdownmenu__section', ...rest, children: [heading && (jsx(Paragraph, { asChild: true, size: size, children: jsx("h2", { id: headingId, className: 'fds-dropdownmenu__heading', children: heading }) })), children] }) }));
});
DropdownMenuGroup.displayName = 'DropdownMenuGroup';

export { DropdownMenuGroup };
