'use client';
import { jsx, jsxs } from 'react/jsx-runtime';
import { ChevronDownIcon } from '@navikt/aksel-icons';
import { clsx } from '../../../node_modules/clsx/dist/lite.js';
import { forwardRef, useContext } from 'react';
import { AccordionItemContext } from '../AccordionItem/AccordionItem.js';
import { Heading } from '../../Typography/Heading/Heading.js';
import { Paragraph } from '../../Typography/Paragraph/Paragraph.js';

const AccordionHeader = forwardRef(({ level = 1, children, className, onHeaderClick, ...rest }, ref) => {
    const context = useContext(AccordionItemContext);
    if (context === null) {
        console.error('<Accordion.Header> has to be used within an <Accordion.Item>');
        return null;
    }
    const handleClick = (e) => {
        context.toggleOpen();
        onHeaderClick && onHeaderClick(e);
    };
    return (jsx(Heading, { ref: ref, size: 'xs', level: level, className: clsx('fds-accordion__header', className), ...rest, children: jsxs("button", { type: 'button', className: clsx('fds-accordion__button', `fds-focus`), onClick: handleClick, "aria-expanded": context.open, "aria-controls": context.contentId, children: [jsx(ChevronDownIcon, { "aria-hidden": true, className: 'fds-accordion__expand-icon', fontSize: '1.5rem' }), jsx(Paragraph, { asChild: true, size: 'sm', children: jsx("span", { children: children }) })] }) }));
});
AccordionHeader.displayName = 'AccordionHeader';

export { AccordionHeader };
