import React, { useState } from 'react';
import { 
  Heading, 
  Paragraph, 
  Card, 
  Button,
  Textfield,
  Tag
} from '@digdir/designsystemet-react';

interface Message {
  text: string;
  sender: 'user' | 'bot';
  timestamp: Date;
}

const FaqChatbot: React.FC = () => {
  const [messages, setMessages] = useState<Message[]>([
    {
      text: 'Hello! How can I help you with Designsystemet today?',
      sender: 'bot',
      timestamp: new Date()
    }
  ]);
  const [input, setInput] = useState('');

  const handleSend = () => {
    if (!input.trim()) return;

    const userMessage: Message = {
      text: input,
      sender: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');

    // Simulate bot response
    setTimeout(() => {
      const botMessage: Message = {
        text: 'I understand you have a question about Designsystemet. Let me help you with that.',
        sender: 'bot',
        timestamp: new Date()
      };
      setMessages(prev => [...prev, botMessage]);
    }, 1000);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  return (
    <div>
      <Heading level={1} size="xlarge" spacing>
        FAQ Chatbot
      </Heading>
      <Paragraph spacing>
        Ask questions about Designsystemet components and get instant answers.
      </Paragraph>

      <Card>
        <div style={{ padding: '1rem' }}>
          <div style={{ height: '400px', overflowY: 'auto', marginBottom: '1rem' }}>
            {messages.map((message, index) => (
              <div
                key={index}
                style={{
                  display: 'flex',
                  justifyContent: message.sender === 'user' ? 'flex-end' : 'flex-start',
                  marginBottom: '1rem'
                }}
              >
                <div
                  style={{
                    maxWidth: '70%',
                    padding: '0.75rem',
                    borderRadius: '8px',
                    backgroundColor: message.sender === 'user' ? '#0067C5' : '#f5f5f5',
                    color: message.sender === 'user' ? 'white' : 'inherit'
                  }}
                >
                  <div style={{ marginBottom: '0.25rem' }}>
                    {message.text}
                  </div>
                  <div
                    style={{
                      fontSize: '0.75rem',
                      opacity: 0.7,
                      textAlign: message.sender === 'user' ? 'right' : 'left'
                    }}
                  >
                    {message.timestamp.toLocaleTimeString()}
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div style={{ display: 'flex', gap: '1rem' }}>
            <Textfield
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Type your question..."
              style={{ flex: 1 }}
            />
            <Button
              variant="primary"
              onClick={handleSend}
              disabled={!input.trim()}
            >
              Send
            </Button>
          </div>
        </div>
      </Card>

      <Card style={{ marginTop: '2rem' }}>
        <div style={{ padding: '1rem' }}>
          <Heading level={2} size="small" spacing>Popular Topics</Heading>
          <div style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
            <Tag size="small" color="first">Components</Tag>
            <Tag size="small" color="second">Styling</Tag>
            <Tag size="small" color="success">Installation</Tag>
            <Tag size="small" color="neutral">Best Practices</Tag>
          </div>
        </div>
      </Card>
    </div>
  );
};

export default FaqChatbot;
