import React from 'react';
import { Paragraph, Link, Loader, Tag } from '@digdir/designsystemet-react';

interface SearchResult {
  title: string;
  summary: string;
  source: string;
  url: string;
}

interface SearchResultsProps {
  results: SearchResult[];
  isLoading: boolean;
}

const SearchResults: React.FC<SearchResultsProps> = ({ results, isLoading }) => {
  if (isLoading) {
    return (
      <div style={{ textAlign: 'center', padding: '2rem' }}>
        <Loader size="medium" title="Searching..." />
      </div>
    );
  }

  if (results.length === 0) {
    return null;
  }

  return (
    <div style={{ marginTop: '2rem' }}>
      {results.map((result, index) => (
        <div
          key={index}
          style={{
            padding: '1rem',
            marginBottom: '1rem',
            borderRadius: '8px',
            backgroundColor: '#f5f5f5',
          }}
        >
          <div style={{ marginBottom: '0.5rem' }}>
            <Tag size="small" color={
              result.source === 'FAQ' ? 'success' :
              result.source === 'Slack' ? 'info' :
              'neutral'
            }>
              {result.source}
            </Tag>
          </div>

          <Link href={result.url} style={{ display: 'block', marginBottom: '0.5rem' }}>
            {result.title}
          </Link>

          <Paragraph size="small" spacing={false}>
            {result.summary}
          </Paragraph>
        </div>
      ))}
    </div>
  );
};

export default SearchResults;
