'use client';
import { jsx } from 'react/jsx-runtime';
import { forwardRef, useContext } from 'react';
import { DropdownMenuContext } from './DropdownMenu.js';
import { Button } from '../Button/Button.js';

const DropdownMenuItem = forwardRef(({ children, className, style, ...rest }, ref) => {
    const { size } = useContext(DropdownMenuContext);
    return (jsx("li", { className: className, style: style, children: jsx(Button, { ref: ref, variant: 'tertiary', size: size, fullWidth: true, className: 'fds-dropdownmenu__item', role: 'menuitem', ...rest, children: children }) }));
});
DropdownMenuItem.displayName = 'DropdownMenuItem';

export { DropdownMenuItem };
