import React from 'react';
import { 
  Paragraph, 
  Link, 
  Tag, 
  Spinner,
  Box,
  Card,
  Heading,
  Divider
} from '@digdir/designsystemet-react';

interface SearchResult {
  title: string;
  summary: string;
  source: string;
  url: string;
}

interface SearchResultsProps {
  results: SearchResult[];
  isLoading: boolean;
}

const SearchResults: React.FC<SearchResultsProps> = ({ results, isLoading }) => {
  if (isLoading) {
    return (
      <Box padding="8" style={{ textAlign: 'center' }}>
        <Spinner size="medium" title="Searching..." variant="interaction" />
      </Box>
    );
  }

  if (results.length === 0) {
    return null;
  }

  return (
    <Box padding="4">
      <Heading level={2} size="small" spacing>
        Search Results ({results.length})
      </Heading>
      <Box padding="2" />

      {results.map((result, index) => (
        <React.Fragment key={index}>
          {index > 0 && <Box padding="2" />}
          <Card border flat>
            <Box padding="4">
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '8px' }}>
                <Link href={result.url} underline={false}>
                  <Heading level={3} size="xsmall" spacing={false}>
                    {result.title}
                  </Heading>
                </Link>
                <Tag size="small" color={
                  result.source === 'FAQ' ? 'success' :
                  result.source === 'Slack' ? 'info' :
                  'neutral'
                }>
                  {result.source}
                </Tag>
              </div>

              <Divider light />

              <Box padding="2" />

              <Paragraph size="small" spacing={false}>
                {result.summary}
              </Paragraph>
            </Box>
          </Card>
        </React.Fragment>
      ))}
    </Box>
  );
};

export default SearchResults;
