import React, { useState } from 'react';
import { 
  Textfield,
  Button,
  Card,
  Heading,
  Paragraph,
  Box
} from '@digdir/designsystemet-react';

interface ChatMessage {
  text: string;
  isUser: boolean;
  timestamp: Date;
}

const FaqChatbot: React.FC = () => {
  const [messages, setMessages] = useState<ChatMessage[]>([
    {
      text: "Hi! I'm the Designsystemet FAQ bot. How can I help you today?",
      isUser: false,
      timestamp: new Date()
    }
  ]);
  const [inputText, setInputText] = useState('');

  const handleSend = () => {
    if (!inputText.trim()) return;

    // Add user message
    const userMessage: ChatMessage = {
      text: inputText,
      isUser: true,
      timestamp: new Date()
    };

    // Simulate bot response
    const botResponse: ChatMessage = {
      text: "I'll help you find an answer to that! (This is a demo response - actual FAQ bot would integrate with a knowledge base)",
      isUser: false,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage, botResponse]);
    setInputText('');
  };

  return (
    <div>
      <Heading level={1} size="xlarge" spacing>FAQ Chatbot</Heading>
      <Paragraph spacing>
        Ask questions about Designsystemet and get instant answers from our FAQ database.
        If I can't answer your question, I'll help escalate it to our design team on Slack.
      </Paragraph>

      <Card>
        <Box padding="4">
          <div style={{ height: '400px', overflowY: 'auto', marginBottom: '1rem' }}>
            {messages.map((message, index) => (
              <div
                key={index}
                style={{
                  display: 'flex',
                  justifyContent: message.isUser ? 'flex-end' : 'flex-start',
                  marginBottom: '1rem'
                }}
              >
                <div
                  style={{
                    maxWidth: '70%',
                    padding: '0.75rem',
                    borderRadius: '8px',
                    backgroundColor: message.isUser ? '#1E88E5' : '#E3F2FD',
                    color: message.isUser ? 'white' : 'black'
                  }}
                >
                  <Paragraph spacing={false}>{message.text}</Paragraph>
                  <div style={{ fontSize: '0.75rem', marginTop: '0.25rem', opacity: 0.7 }}>
                    {message.timestamp.toLocaleTimeString()}
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div style={{ display: 'flex', gap: '1rem' }}>
            <div style={{ flex: 1 }}>
              <Textfield
                label=""
                value={inputText}
                onChange={(e) => setInputText(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSend()}
                placeholder="Type your question here..."
              />
            </div>
            <Button
              onClick={handleSend}
              disabled={!inputText.trim()}
            >
              Send
            </Button>
          </div>
        </Box>
      </Card>
    </div>
  );
};

export default FaqChatbot;
