'use client';
'use strict';

var jsxRuntime = require('react/jsx-runtime');
var React = require('react');
var DropdownMenu = require('./DropdownMenu.js');
var Button = require('../Button/Button.js');

const DropdownMenuItem = React.forwardRef(({ children, className, style, ...rest }, ref) => {
    const { size } = React.useContext(DropdownMenu.DropdownMenuContext);
    return (jsxRuntime.jsx("li", { className: className, style: style, children: jsxRuntime.jsx(Button.Button, { ref: ref, variant: 'tertiary', size: size, fullWidth: true, className: 'fds-dropdownmenu__item', role: 'menuitem', ...rest, children: children }) }));
});
DropdownMenuItem.displayName = 'DropdownMenuItem';

exports.DropdownMenuItem = DropdownMenuItem;
