'use client';
'use strict';

var jsxRuntime = require('react/jsx-runtime');
var React = require('react');
var index = require('../List/index.js');
var ErrorSummaryRoot = require('./ErrorSummaryRoot.js');

const ErrorSummaryHeading = ({ id, ...rest }) => {
    const { headingId, setHeadingId } = React.useContext(ErrorSummaryRoot.ErrorSummaryContext);
    React.useEffect(() => {
        if (id && headingId !== id) {
            setHeadingId(id);
        }
    }, [headingId, id, setHeadingId]);
    return (jsxRuntime.jsx(index.List.Heading, { ...rest, id: headingId }));
};
ErrorSummaryHeading.displayName = 'ErrorSummaryHeading';

exports.ErrorSummaryHeading = ErrorSummaryHeading;
