'use client';
'use strict';

var jsxRuntime = require('react/jsx-runtime');
var React = require('react');
var ListRoot = require('./ListRoot.js');
var Heading = require('../Typography/Heading/Heading.js');

const HEADING_SIZE_MAP = {
    sm: '2xs',
    md: 'xs',
    lg: 'sm',
};
const ListHeading = React.forwardRef(({ level = 2, id, ...rest }, ref) => {
    const { size, headingId, setHeadingId } = React.useContext(ListRoot.ListContext);
    const randomId = React.useId();
    const headingId_ = id ?? randomId;
    const headingSize = React.useMemo(() => HEADING_SIZE_MAP[size], [size]);
    React.useEffect(() => {
        if (headingId !== headingId_) {
            setHeadingId(headingId_);
        }
    }, [headingId, id, setHeadingId, headingId_]);
    return (jsxRuntime.jsx(Heading.Heading, { ref: ref, size: headingSize, id: headingId, level: level, spacing: true, ...rest }));
});
ListHeading.displayName = 'ListHeading';

exports.ListHeading = ListHeading;
