'use client';
'use strict';

var jsxRuntime = require('react/jsx-runtime');
var React = require('react');
var PaginationRoot = require('./PaginationRoot.js');
var Button = require('../Button/Button.js');

const PaginationButton = React.forwardRef(({ isActive, ...rest }, ref) => {
    const { size } = React.useContext(PaginationRoot.PaginationContext);
    return (jsxRuntime.jsx(Button.Button, { ref: ref, variant: isActive ? 'primary' : 'tertiary', "aria-current": isActive, color: 'first', size: size, ...rest }));
});

exports.PaginationButton = PaginationButton;
