'use client';
import { jsx } from 'react/jsx-runtime';
import { useContext, useEffect } from 'react';
import { List } from '../List/index.js';
import { ErrorSummaryContext } from './ErrorSummaryRoot.js';

const ErrorSummaryHeading = ({ id, ...rest }) => {
    const { headingId, setHeadingId } = useContext(ErrorSummaryContext);
    useEffect(() => {
        if (id && headingId !== id) {
            setHeadingId(id);
        }
    }, [headingId, id, setHeadingId]);
    return (jsx(List.Heading, { ...rest, id: headingId }));
};
ErrorSummaryHeading.displayName = 'ErrorSummaryHeading';

export { ErrorSummaryHeading };
